import PersistentStorage from "./Storage";
import {Constants} from "../helpers/Models";
import {isExpired} from "react-jwt";
import {isLoggedInVar, loggedUserVar, tokenVar} from "../apollo/GqlClient";

export default class Auth {

	private storage = PersistentStorage.instance;

	static _instance: Auth | null = null;

	constructor() {
	}

	public static get instance(): Auth {
		if (!this._instance) {
			this._instance = new Auth()
		}
		return this._instance
	}

	public get authorized(): boolean {
		const token = tokenVar();//this.storage.getItem(Constants.TOKEN);
		if (token && !isExpired(token)) {
			const expired = isExpired(token);
			debugger;
			return true;
		}
		return false;
	}

	public get initialAuthorized(): boolean {
		const token = this.storage.getItem(Constants.TOKEN);
		if (token && !isExpired(token)) {
			const expired = isExpired(token);
			debugger;
			return true;
		}
		return false;
	}

	public logOut(): void {
		this.storage.clear();
		loggedUserVar(null);
		tokenVar(null);
		isLoggedInVar(false);
	}

	public get initialToken(): string | null {
		const token = this.storage.getItem(Constants.TOKEN);
		return token ? token : null
	}

	public get token(): string | null {
		// const token = this.storage.getItem(Constants.TOKEN);
		// return token ? token : null
		return tokenVar()
	}
	public set token(token: string | null) {
		if (token) {
			this.storage.setItem(Constants.TOKEN, token as string);
		} else {
			this.storage.removeItem(Constants.TOKEN);
		}
		tokenVar(token);
	}

	public get initialUser(): any {
		const user = this.storage.getItem(Constants.USER);
		return user ? user : null;
	}

	public get user(): any {
		return loggedUserVar()
	}

}
