import React, {useEffect, useState} from "react";
import {Navbar} from "react-bootstrap";
import {routes} from "../../process/helpers/Routes";
import {BoxArrowRight} from "react-bootstrap-icons";
import PersistentStorage from "../../process/store/Storage";
import {loggedUserVar} from "../../process/apollo/GqlClient";
import {IUser} from "../../process/helpers/Models";
import {AuthService} from "../../process/services/AuthService";

export const TopBar = ({title}: any) => {

	const [currentUser, setCurrentUser] = useState<IUser | null>(null);

	useEffect(() => {
		const user = loggedUserVar();
		setCurrentUser(user);
	}, [])

	const storage = PersistentStorage.instance;
	const menuItems = Object.values(routes).filter(value => (value.path !== '/users/new' && value.path !== '/users/:id'))

	const logout = () => {
		AuthService.logOut();
	}

	return (
		<Navbar bg="light" expand="lg">
			<Navbar.Brand>{title}</Navbar.Brand>
			<Navbar.Toggle aria-controls="basic-navbar-nav" />
			<Navbar.Collapse id="basic-navbar-nav" className="justify-content-end">
				<Navbar.Text>
					<span className="pr-3">
						{currentUser ? `User: ${currentUser.phone}` : ''}
					</span>
					<BoxArrowRight className="pointer" style={{ fontSize: '1.75em' }} onClick={logout} />
				</Navbar.Text>
			</Navbar.Collapse>
		</Navbar>

	)
}
