import PersistentStorage from "../store/Storage";
import {isLoggedInVar, loggedUserVar, tokenVar} from "../apollo/GqlClient";
import {Constants} from "../helpers/Models";
import {isExpired} from "react-jwt";

const storage = PersistentStorage.instance;

export const AuthService = {
	logOut: () => {
		storage.clear();
		loggedUserVar(null);
		tokenVar(null);
		isLoggedInVar(false);
	},

	setToken: (token: string) => {
		storage.setItem(Constants.TOKEN, token as string);
		tokenVar(token);
	},

	getToken: (): string | null => {
		const token = storage.getItem(Constants.TOKEN);
		if (token) {
			return token;
		}
		return null
	},

	setMe: (me: any) => {
		storage.setItem(Constants.USER, me);
		loggedUserVar(me);
	},

	getMe: (): any => {
		const user = storage.getItem(Constants.USER);
		if (user) {
			return user;
		}
		return null;
	},

	setLoggedId: (flag: boolean) => {
		isLoggedInVar(flag);
	},

	isAuthorized: (): boolean => {
		debugger;
		const token = storage.getItem(Constants.TOKEN);
		if (token && !isExpired(token)) {
			const expired = isExpired(token);
			debugger;
			return true;
		}
		return false;
	}

}
